", pstrLogicClassName ); NF_SHARE_PTR < NFIClass > pClass ( NF_NEW NFClass ( pstrLogicClassName )); // 新建 ID属性值的对象，将其存入到当前module的map中。 AddElement ( pstrLogicClassName , pClass ); pClass -> SetParent ( pParentClass ); pClass -> SetInstancePath ( pstrInstancePath ); // 从 NFDataCfg/Struct 加载文件内容，将内容保存到pclass中 AddClass ( pstrPath , pClass ); for ( rapidxml :: xml_node <>* pDataNode = attrNode -> first_node (); pDataNode ; pDataNode = pDataNode -> next_sibling ()) { //her children Load ( pDataNode , pClass ); } //printf( "-----------------------------------------------------
"); return true ; } bool NFClassModule :: Load () { ////////////////////////////////////////////////////////////////////////// std :: string strFile = pPluginManager -> GetConfigPath () + msConfigFileName ; std :: string strContent ; pPluginManager -> GetFileContent ( strFile , strContent ); rapidxml :: xml_document <> xDoc ; xDoc . parse < 0 > (( char * ) strContent . c_str ()); ////////////////////////////////////////////////////////////////////////// //support for unlimited layer class inherits rapidxml :: xml_node <>* root = xDoc . first_node (); // 递归每个class对象 for ( rapidxml :: xml_node <>* attrNode = root -> first_node (); attrNode ; attrNode = attrNode -> next_sibling ()) { Load ( attrNode , NULL ); } if ( m_pBackupClassModule ) m_pBackupClassModule -> Load (); return true ; } bool NFClassModule :: Save () { if ( m_pBackupClassModule ) m_pBackupClassModule -> Save (); return true ; } NF_SHARE_PTR < NFIPropertyManager > NFClassModule :: GetClassPropertyManager ( const std :: string & strClassName ) { NF_SHARE_PTR < NFIClass > pClass = GetElement ( strClassName ); if ( pClass ) { return pClass -> GetPropertyManager (); } return NULL ; } NF_SHARE_PTR < NFIRecordManager > NFClassModule :: GetClassRecordManager ( const std :: string & strClassName ) { NF_SHARE_PTR < NFIClass > pClass = GetElement ( strClassName ); if ( pClass ) { return pClass -> GetRecordManager (); } return NULL ; } bool NFClassModule :: Clear () { if ( m_pBackupClassModule ) m_pBackupClassModule -> Clear (); return true ; } NFIClassModule * NFClassModule :: GetBackupClassModule () { return m_pBackupClassModule ; } bool NFClassModule :: AddClassCallBack ( const std :: string & strClassName , const CLASS_EVENT_FUNCTOR_PTR & cb ) { NF_SHARE_PTR < NFIClass > pClass = GetElement ( strClassName ); if ( nullptr == pClass ) { return false ; } // 将回调函数 添加到 List return pClass -> AddClassCallBack ( cb ); } // 不断执行 list 里面的回调函数 bool NFClassModule :: DoEvent ( const NFGUID & objectID , const std :: string & strClassName , const CLASS_OBJECT_EVENT eClassEvent , const NFDataList & valueList ) { NF_SHARE_PTR < NFIClass > pClass = GetElement ( strClassName ); if ( nullptr == pClass ) { return false ; } return pClass -> DoEvent ( objectID , eClassEvent , valueList ); }

