", stacktrace[i]); LOG ( FATAL ) << stacktrace [ i ]; } free ( stacktrace ); } #endif NFPluginServer :: NFPluginServer ( const std :: string & strArgv ) { this -> strArgvList = strArgv ; //创建输出dump信息 #if NF_PLATFORM == NF_PLATFORM_WIN SetUnhandledExceptionFilter (( LPTOP_LEVEL_EXCEPTION_FILTER ) ApplicationCrashHandler ); #else signal ( SIGSEGV , NFCrashHandler ); //el::Helpers::setCrashHandler(CrashHandler); #endif } void NFPluginServer :: Execute () { #if NF_PLATFORM == NF_PLATFORM_WIN __try #else try #endif { pPluginManager -> Execute (); } #if NF_PLATFORM == NF_PLATFORM_WIN __except ( ApplicationCrashHandler ( GetExceptionInformation ())) { } #else catch ( const std :: exception & e ) { NFException :: StackTrace ( 11 ); } catch (...) { NFException :: StackTrace ( 11 ); } #endif } void NFPluginServer :: PrintfLogo () { #if NF_PLATFORM == NF_PLATFORM_WIN SetConsoleTextAttribute ( GetStdHandle ( STD_OUTPUT_HANDLE ), FOREGROUND_INTENSITY | FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE ); #endif std :: cout << " 
