 " << std :: endl ; #if NF_PLATFORM == NF_PLATFORM_WIN SetConsoleTextAttribute ( GetStdHandle ( STD_OUTPUT_HANDLE ), FOREGROUND_INTENSITY | FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE ); #endif } void NFPluginServer :: SeMidWareLoader ( std :: function < void ( NFIPluginManager * p ) > fun ) { externalPluginLoader = fun ; } void NFPluginServer :: Init () { PrintfLogo (); //每一个NFPluginServer只有一个NFPluginManager pPluginManager = NF_SHARE_PTR < NFIPluginManager > ( NF_NEW NFPluginManager ()); // 创建新的控制台处理NFPluginServer的参数，这个参数可能来自命令行或者构造函数 ProcessParameter (); //设置读取文件内容的函数，用来读取xml内容为字符串 pPluginManager -> SetGetFileContentFunctor ( GetFileContent ); pPluginManager -> SetConfigPath ( "../" ); pPluginManager -> LoadPluginConfig (); if ( externalPluginLoader ) { externalPluginLoader ( pPluginManager . get ()); } pPluginManager -> LoadPlugin (); pPluginManager -> Awake (); pPluginManager -> Init (); pPluginManager -> AfterInit (); pPluginManager -> CheckConfig (); pPluginManager -> ReadyExecute (); } void NFPluginServer :: Final () { pPluginManager -> BeforeShut (); pPluginManager -> Shut (); pPluginManager -> Finalize (); pPluginManager = nullptr ; } void NFPluginServer :: ProcessParameter () { //设置处理控制台消息 #if NF_PLATFORM == NF_PLATFORM_WIN SetConsoleCtrlHandler (( PHANDLER_ROUTINE ) ApplicationCtrlHandler , true ); #else //run it as a daemon process if ( strArgvList . find ( "-d" ) != string :: npos ) { InitDaemon (); } signal ( SIGPIPE , SIG_IGN ); signal ( SIGCHLD , SIG_IGN ); #endif NFDataList argList ; argList . Split ( this -> strArgvList , " " ); for ( int i = 0 ; i < argList . GetCount (); i ++ ) { strPluginName = argList . String ( i ); if ( strPluginName . find ( "Plugin=" ) != string :: npos ) { strPluginName . erase ( 0 , 7 ); break ; } strPluginName = "" ; } //将 Plugin.xml传进去 pPluginManager -> SetConfigName ( strPluginName ); for ( int i = 0 ; i < argList . GetCount (); i ++ ) { strAppName = argList . String ( i ); if ( strAppName . find ( "Server=" ) != string :: npos ) { strAppName . erase ( 0 , 7 ); break ; } strAppName = "" ; } //设置app的名字 pPluginManager -> SetAppName ( strAppName ); for ( int i = 0 ; i < argList . GetCount (); i ++ ) { strAppID = argList . String ( i ); if ( strAppID . find ( "ID=" ) != string :: npos ) { strAppID . erase ( 0 , 3 ); break ; } strAppID = "" ; } int nAppID = 0 ; if ( NF_StrTo ( strAppID , nAppID )) { pPluginManager -> SetAppID ( nAppID ); } // NoSqlServer.xml:IP="127.0.0.1"==IP="192.168.1.1" if ( strArgvList . find ( ".xml:" ) != string :: npos ) { for ( int i = 0 ; i < argList . GetCount (); i ++ ) { std :: string strPipeline = argList . String ( i ); int posFile = strPipeline . find ( ".xml:" ); int posContent = strPipeline . find ( "==" ); if ( posFile != string :: npos && posContent != string :: npos ) { std :: string fileName = strPipeline . substr ( 0 , posFile + 4 ); std :: string content = strPipeline . substr ( posFile + 5 , posContent - ( posFile + 5 )); std :: string replaceContent = strPipeline . substr ( posContent + 2 , strPipeline . length () - ( posContent + 2 )); pPluginManager -> AddFileReplaceContent ( fileName , content , replaceContent ); } } } std :: string strDockerFlag = "0" ; for ( int i = 0 ; i < argList . GetCount (); i ++ ) { strDockerFlag = argList . String ( i ); if ( strDockerFlag . find ( "Docker=" ) != string :: npos ) { strDockerFlag . erase ( 0 , 7 ); break ; } strDockerFlag = "" ; } int nDockerFlag = 0 ; if ( NF_StrTo ( strDockerFlag , nDockerFlag )) { pPluginManager -> SetRunningDocker ( nDockerFlag ); } strTitleName = strAppName + strAppID ; // +" PID" + NFGetPID(); if ( ! strTitleName . empty ()) { //去掉字符串中的Server strTitleName . replace ( strTitleName . find ( "Server" ), 6 , "" ); strTitleName = "NF" + strTitleName ; } else { strTitleName = "NFIDE" ; } #if NF_PLATFORM == NF_PLATFORM_WIN SetConsoleTitle ( strTitleName . c_str ()); #elif NF_PLATFORM == NF_PLATFORM_LINUX prctl ( PR_SET_NAME , strTitleName . c_str ()); //setproctitle(strTitleName.c_str()); #endif } void NFPluginServer :: InitDaemon () { //linux下创建守护进程 #if NF_PLATFORM != NF_PLATFORM_WIN daemon ( 1 , 0 ); // ignore signals signal ( SIGINT , SIG_IGN ); signal ( SIGHUP , SIG_IGN ); signal ( SIGQUIT , SIG_IGN ); signal ( SIGPIPE , SIG_IGN ); signal ( SIGTTOU , SIG_IGN ); signal ( SIGTTIN , SIG_IGN ); signal ( SIGTERM , SIG_IGN ); #endif } bool NFPluginServer :: GetFileContent ( NFIPluginManager * p , const std :: string & strFilePath , std :: string & strContent ) { FILE * fp = fopen ( strFilePath . c_str (), "rb" ); if ( ! fp ) { return false ; } //定位到文件的结尾处 fseek ( fp , 0 , SEEK_END ); //得到文件内容的大小 const long filelength = ftell ( fp ); //定位到文件的开头 fseek ( fp , 0 , SEEK_SET ); //将字符串设置成相应的大小 strContent . resize ( filelength ); //将文件内容读取到字符串中 fread (( void * ) strContent . data (), filelength , 1 , fp ); fclose ( fp ); //../NFDataCfg/Debug/Plugin.xml的子串 Plugin.xml std :: string strFileName = strFilePath . substr ( strFilePath . find_last_of ( "/ \ " ) + 1 ); //content为空 std :: vector < NFReplaceContent > contents = p -> GetFileReplaceContents ( strFileName ); if ( ! contents . empty ()) { for ( auto it : contents ) { std :: size_t pos = strContent . find ( it . content ); if ( pos != string :: npos ) { strContent . replace ( pos , it . content . length (), it . newValue . c_str ()); } } } return true ; }
